import React, { useState } from 'react';
import Header from './components/Header';
import ContactList from './components/ContactList';

const initialList = [
  { firstName: 'Harry', lastName: 'Potter', phone: '222222', id: '1' },
  { firstName: 'Tom', lastName: 'Riddle', phone: '111111', id: '2' },
  { firstName: 'Johnny', lastName: 'Depp', phone: '12345673', id: '3' },
];

const App = () => {
  const [contacts, setContacts] = useState(initialList);
  const [editingContact, setEditingContact] = useState(null);

  const addContact = contact => {
    setContacts([...contacts, contact]);
  };

  const updateContact = (id, updatedContact) => {
    setContacts(
      contacts.map(contact => (contact.id === id ? updatedContact : contact))
    );
    setEditingContact(null);
  };

  const deleteContact = id => {
    setContacts(contacts.filter(contact => contact.id !== id));
  };

  return (
    <div>
      <Header onAddContact={addContact} />
      <ContactList
        contacts={contacts}
        onEditContact={setEditingContact}
        onDeleteContact={deleteContact}
      />
      {editingContact && (
        <EditContactForm
          contact={editingContact}
          onUpdateContact={updateContact}
          onCancel={() => setEditingContact(null)}
        />
      )}
    </div>
  );
};

const EditContactForm = ({ contact, onUpdateContact, onCancel }) => {
  const [firstName, setFirstName] = useState(contact.firstName);
  const [lastName, setLastName] = useState(contact.lastName);
  const [phone, setPhone] = useState(contact.phone);

  const onSubmit = e => {
    e.preventDefault();
    onUpdateContact(contact.id, { firstName, lastName, phone, id: contact.id });
  };

  return (
    <div>
      <h2>Edit Contact</h2>
      <form onSubmit={onSubmit}>
        <input
          type="text"
          placeholder="First Name"
          value={firstName}
          onChange={e => setFirstName(e.target.value)}
        />
        <input
          type="text"
          placeholder="Last Name"
          value={lastName}
          onChange={e => setLastName(e.target.value)}
        />
        <input
          type="text"
          placeholder="Phone"
          value={phone}
          onChange={e => setPhone(e.target.value)}
        />
        <button type="submit">Update Contact</button>
        <button onClick={onCancel}>Cancel</button>
      </form>
    </div>
  );
};

export default App;
