import React, { useState } from 'react';
import './App.css';
import Header from './components/Header';
import ContactList from './components/ContactList';

const initialList = [{ "firstName": "Harry", "lastName": "Potter", "phone": "222222", "id": "1" }, { "firstName": "Tom", "lastName": "Riddle", "phone": "111111", "id": "2" }, { "firstName": "Johnny", "lastName": "Depp", "phone": "12345673", "id": "3" }];

function App() {
  const [contacts, setContacts] = useState(initialList);

  const addContact = (newContact) => {
    setContacts([...contacts, newContact]);
  };

  return (
    <div className="App">
      <Header addContact={addContact} />
      <ContactList contacts={contacts} />
    </div>
  );
}

export default App;
