import React, { useState } from "react";
import ContactList from "./components/ContactList";
import Header from "./components/Header";

function App() {
  const [contacts, setContacts] = useState(initialList);

  const handleAddContact = (newContact) => {
    setContacts([...contacts, newContact]);
  };

  const handleDeleteContact = (id) => {
    const updatedContacts = contacts.filter((contact) => contact.id !== id);
    setContacts(updatedContacts);
  };

  const handleUpdateContact = (id, updatedContact) => {
    const updatedContacts = contacts.map((contact) => {
      if (contact.id === id) {
        return updatedContact;
      } else {
        return contact;
      }
    });
    setContacts(updatedContacts);
  };

  return (
    <div>
      <Header onAdd={handleAddContact} />
      <ContactList
        contacts={contacts}
        onDelete={handleDeleteContact}
        onUpdate={handleUpdateContact}
      />
    </div>
  );
}

const initialList = [
  { firstName: "Harry", lastName: "Potter", phone: "222222", id: "1" },
  { firstName: "Tom", lastName: "Riddle", phone: "111111", id: "2" },
  { firstName: "Johnny", lastName: "Depp", phone: "12345673", id: "3" },
];

export default App;
