import React from 'react';

const ToDoItem = ({ item, onDeleteTodo, onUpdateTodo, setTodoEdit }) => {
    const handleDeleteTodo = () => {
        onDeleteTodo(item.id);
    };

    const handleUpdateTodo = () => {
        setTodoEdit({ id: item.id, title: item.title });
    };

    const handleToggleTodo = () => {
        onUpdateTodo(item.id, item.title, !item.done);
    };

    return (
        <li className={item.done ? 'done' : ''}>
            <span className="title">{item.title}</span>
            <div>
                <button onClick={handleDeleteTodo}>Delete</button>
                <button onClick={handleUpdateTodo}>Edit</button>
                <button onClick={handleToggleTodo}>
                    {item.done ? 'Undone' : 'Done'}
                </button>
            </div>
        </li>
    );
};

export default ToDoItem;
