import React from 'react';
import ToDoItem from './ToDoItem';

function ToDoList({ toDoList, deleteTodo, updateTodo }) {
    const handleUpdate = (id) => {
        updateTodo(id, true);
    };

    return (
        <ul>
            {toDoList.map((item) => (
                <ToDoItem key={item.id} todo={item} deleteTodo={deleteTodo} updateTodo={updateTodo} handleUpdate={handleUpdate} />
            ))}
        </ul>
    );
}

export default ToDoList;
