import React from 'react';
import ToDoItem from './ToDoItem';

function ToDoList({ todos, deleteTodo, updateTodo }) {
    const handleUpdate = (id, done) => {
        updateTodo(id, done);
    };

    return (
        <ul>
            {todos.map((item) => (
                <ToDoItem
                    key={item.id}
                    todo={item}
                    deleteTodo={deleteTodo}
                    updateTodo={handleUpdate}
                />
            ))}
        </ul>
    );
}

export default ToDoList;
