import React from 'react';
import ToDoItem from './ToDoItem';

function ToDoList({ todos, deleteTodo, updateTodo }) {
    return (
        <ul>
            {todos.map((todo) => (
                <ToDoItem key={todo.id} todo={todo} deleteTodo={deleteTodo} updateTodo={updateTodo} />
            ))}
        </ul>
    );
}

export default ToDoList;
