import React, { useState } from 'react';
import ToDoItem from './ToDoItem';

const ToDoList = ({ initialList }) => {
    const [list, setList] = useState(initialList);
    const [todoEdit, setTodoEdit] = useState(null);

    const handleDelete = (id) => {
        const newList = list.filter((item) => item.id !== id);
        setList(newList);
    };

    const handleEdit = (todo) => {
        setTodoEdit(todo);
    };

    const handleAddTodo = (title) => {
        const todo = {
            title,
            status: true,
            done: false,
            id: Math.random(),
            toDo: '',
        };
        setList([...list, todo]);
    };

    const handleUpdate = (title) => {
        const newList = list.map((item) => {
            if (item.id === todoEdit.id) {
                return { ...item, title };
            }
            return item;
        });
        setList(newList);
        setTodoEdit(null);
    };

    return (
        <div>
            <ul>
                {list.map((todo) => (
                    <ToDoItem
                        key={todo.id}
                        todo={todo}
                        handleDelete={handleDelete}
                        handleEdit={handleEdit}
                    />
                ))}
            </ul>
            <div>
                {todoEdit ? (
                    <form onSubmit={(e) => {
                        e.preventDefault();
                        handleUpdate(e.target.title.value);
                    }}>
                        <input type="text" name="title" defaultValue={todoEdit.title} />
                        <button type="submit">Update</button>
                        <button onClick={() => setTodoEdit(null)}>Cancel</button>
                    </form>
                ) : (
                    <form onSubmit={(e) => {
                        e.preventDefault();
                        handleAddTodo(e.target.title.value);
                        e.target.title.value = '';
                    }}>
                        <input type="text" name="title" />
                        <button type="submit">Add</button>
                    </form>
                )}
            </div>
        </div>
    );
};

export default ToDoList;
