import { useState } from 'react';
import './App.css';
import Header from './components/Header';
import ToDoList from './components/ToDoList';

const initialList = [
  {
    "title": "facere blanditiis illumvddv",
    "status": true,
    "done": false,
    "id": "96",
    "toDo": ""
  },
  {
    "title": "itaque eveniet rationevdvdsvds",
    "status": true,
    "done": true,
    "id": "98",
    "toDo": ""
  },
  {
    "title": "molestiae nemo minus",
    "status": true,
    "done": true,
    "id": "99",
    "toDo": ""
  },
];

function App() {
  const [todos, setTodos] = useState(initialList);
  const [todoEdit, setTodoEdit] = useState(null);

  const handleAddTodo = (title) => {
    const todo = {
      title,
      status: false,
      done: false,
      id: Math.random(),
      toDo: "",
    };
    setTodos([...todos, todo]);
  };

  const handleDeleteTodo = (id) => {
    const filteredTodos = todos.filter((todo) => todo.id !== id);
    setTodos(filteredTodos);
  };

  const handleToggleDone = (id) => {
    const updatedTodos = todos.map((todo) => {
      if (todo.id === id) {
        return {
          ...todo,
          done: !todo.done,
        };
      }
      return todo;
    });
    setTodos(updatedTodos);
  };

  const handleEditTodo = (todo) => {
    setTodoEdit(todo);
  };

  const handleUpdateTodo = (id, title) => {
    const updatedTodos = todos.map((todo) => {
      if (todo.id === id) {
        return {
          ...todo,
          title,
        };
      }
      return todo;
    });
    setTodos(updatedTodos);
    setTodoEdit(null);
  };

  return (
    <div className="App">
      <Header onTodoSubmit={handleAddTodo} todoEdit={todoEdit} onUpdateTodo={handleUpdateTodo} />
      <ToDoList todos={todos} onDeleteTodo={handleDeleteTodo} onToggleDone={handleToggleDone} onEditTodo={handleEditTodo} />
    </div>
  );
}

export default App;
