import React from 'react';
import ToDoItem from './ToDoItem';

function ToDoList({ list, onUpdate, onDelete }) {
    return (
        <ul>
            {list.map((item) => (
                <ToDoItem key={item.id} todo={item} deleteTodo={onDelete} updateTodo={onUpdate} />
            ))}
        </ul>
    );
}

export default ToDoList;
