import React, { useState } from 'react';
import ToDoItem from './ToDoItem';

const ToDoList = ({ list, onAddTodo, onDeleteTodo, onUpdateTodo }) => {
  const [todoEdit, setTodoEdit] = useState({ id: null, title: '' });

  const handleAddTodo = (e) => {
    e.preventDefault();

    if (!todoEdit.title.trim()) {
      return;
    }

    const todo = {
      ...todoEdit,
      id: Math.random(),
    };

    onAddTodo(todo);
    setTodoEdit({ id: null, title: '' });
  };

  const handleDeleteTodo = (id) => {
    onDeleteTodo(id);
  };

  const handleUpdateTodo = (id, title) => {
    const todo = {
      id,
      title,
    };
    onUpdateTodo(todo);
    setTodoEdit({ id: null, title: '' });
  };

  return (
    <div className="todo-list">
      <h2>Todo List</h2>
      <ul>
        {list.map((item) => (
          <ToDoItem
            key={item.id}
            item={item}
            onDeleteTodo={handleDeleteTodo}
            onUpdateTodo={handleUpdateTodo}
            setTodoEdit={setTodoEdit}
          />
        ))}
      </ul>
      <form onSubmit={handleAddTodo}>
        <input
          type="text"
          placeholder="Add a new todo"
          value={todoEdit.title}
          onChange={(e) =>
            setTodoEdit({ ...todoEdit, title: e.target.value })
          }
        />
        <button type="submit">Add</button>
      </form>
    </div>
  );
};

export default ToDoList;
