import React from "react";

function Footer({ count, filter, setFilter }) {
    const handleFilterChange = (event) => {
        setFilter(event.target.value);
    };

    const filteredCount = () => {
        switch (filter) {
            case "done":
                return count.done;
            case "notDone":
                return count.notDone;
            default:
                return count.all;
        }
    };

    return (
        <footer>
            {filteredCount()} items left
            <div className="footer-text">
                <p>Â© 2023 My Website. All rights reserved.</p>
                <div>
                    <button
                        onClick={handleFilterChange}
                        value="done"
                        className={filter === "done" ? "active" : ""}
                    >
                        Only Done
                    </button>
                    <button
                        onClick={handleFilterChange}
                        value="all"
                        className={filter === "all" ? "active" : ""}
                    >
                        All
                    </button>
                    <button
                        onClick={handleFilterChange}
                        value="notDone"
                        className={filter === "notDone" ? "active" : ""}
                    >
                        Not Done
                    </button>
                </div>
            </div>
        </footer>
    );
}

export default Footer;
