import React from "react";

function Footer({ count, filter, setFilter }) {
    const handleFilterChange = (filter) => {
        setFilter(filter);
    };

    return (
        <footer>
            {count} items left
            <div className="footer-text">
                <button onClick={() => handleFilterChange("all")} className={filter === "all" ? "active" : ""}>
                    All
                </button>
                <button onClick={() => handleFilterChange("done")} className={filter === "done" ? "active" : ""}>
                    Done
                </button>
                <button onClick={() => handleFilterChange("not_done")} className={filter === "not_done" ? "active" : ""}>
                    Not Done
                </button>
            </div>
        </footer>
    );
}

export default Footer;
